import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BusService } from '../../services/bus.service';
import { Ticket } from '../../models/bus.model';

@Component({
  selector: 'app-booking-success',
  templateUrl: './booking-success.component.html',
  styleUrls: ['./booking-success.component.css']
})
export class BookingSuccessComponent implements OnInit {
  ticket: Ticket | null = null;
  loading = true;
  errorMessage = '';
  selectedSeats: string[] = [];
  bookedFromApi: string[] = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private busService: BusService
  ) {}

  ngOnInit(): void {
    const ticketId = this.route.snapshot.paramMap.get('id');
    if (ticketId) {
      this.loadTicket(ticketId);
    } else {
      this.errorMessage = 'Invalid ticket ID';
      this.loading = false;
    }
  }

  loadTicket(ticketId: string): void {
    this.busService.getTicket(ticketId).subscribe({
      next: (ticket) => {
        this.ticket = ticket;
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading ticket:', error);
        this.errorMessage = 'Failed to load ticket details';
        this.loading = false;
      }
    });
  }

  printTicket(): void {
    window.print();
  }

  goToHome(): void {
    this.router.navigate(['/']);
  }

  formatTime(time: string): string {
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const formattedHour = hour % 12 || 12;
    return `${formattedHour}:${minutes} ${ampm}`;
  }

  formatDate(date: string): string {
    const d = new Date(date);
    return d.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  getStatusText(status: number): string {
    switch(status) {
      case 0: return 'Pending';
      case 1: return 'Confirmed';
      case 2: return 'Cancelled';
      default: return 'Unknown';
    }
  }

  getStatusClass(status: number): string {
    switch(status) {
      case 0: return 'status-pending';
      case 1: return 'status-confirmed';
      case 2: return 'status-cancelled';
      default: return '';
    }
  }

  onSeatsChanged(seats: string[]) {
    this.selectedSeats = seats;
  }
}
<div class="container">
  <div class="loading-spinner" *ngIf="loading">
    <div class="spinner"></div>
    <p>Loading ticket details...</p>
  </div>

  <div class="error-alert" *ngIf="errorMessage">
    {{ errorMessage }}
  </div>

  <div class="success-page" *ngIf="!loading && ticket">
    <div class="success-animation">
      <div class="checkmark-circle">
        <div class="checkmark">✓</div>
      </div>
      <h1>Booking Confirmed!</h1>
      <p>Your ticket has been successfully booked</p>
    </div>

    <div class="ticket-card" id="ticket-print">
      <div class="ticket-header">
        <div class="ticket-logo">🎫</div>
        <div class="ticket-title">
          <h2>E-Ticket</h2>
          <p class="ticket-id">ID: {{ ticket.id }}</p>
        </div>
        <div [class]="'status-badge ' + getStatusClass(ticket.status)">
          {{ getStatusText(ticket.status) }}
        </div>
      </div>

      <div class="ticket-divider"></div>

      <div class="ticket-body">
        <div class="company-info">
          <h3>{{ ticket.companyName }}</h3>
          <p>{{ ticket.busName }}</p>
        </div>

        <div class="journey-details">
          <div class="journey-row">
            <div class="journey-point">
              <span class="label">From</span>
              <span class="value">{{ ticket.fromCity }}</span>
              <span class="boarding">Boarding: {{ ticket.boardingPoint }}</span>
            </div>

            <div class="journey-arrow">
              <div class="arrow-line"></div>
              <div class="arrow-head">→</div>
            </div>

            <div class="journey-point">
              <span class="label">To</span>
              <span class="value">{{ ticket.toCity }}</span>
              <span class="dropping">Dropping: {{ ticket.droppingPoint }}</span>
            </div>
          </div>
        </div>

        <div class="ticket-details-grid">
          <div class="detail-item">
            <span class="detail-label">📅 Journey Date</span>
            <span class="detail-value">{{ formatDate(ticket.journeyDate) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">🕐 Departure Time</span>
            <span class="detail-value">{{ formatTime(ticket.startTime) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">🕐 Arrival Time</span>
            <span class="detail-value">{{ formatTime(ticket.arrivalTime) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">💺 Seat Number</span>
            <span class="detail-value seat-highlight">{{ ticket.seatNumber }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">👤 Passenger Name</span>
            <span class="detail-value">{{ ticket.passengerName }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">📱 Mobile Number</span>
            <span class="detail-value">{{ ticket.mobileNumber }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">📅 Booking Date</span>
            <span class="detail-value">{{ formatDate(ticket.bookingDate) }}</span>
          </div>

          <div class="detail-item price-item">
            <span class="detail-label">💰 Fare</span>
            <span class="detail-value price-value">৳{{ ticket.price }}</span>
          </div>
        </div>
      </div>

      <div class="ticket-divider"></div>

      <div class="ticket-footer">
        <p class="footer-note">
          ?? Please carry a valid ID proof during your journey
        </p>
        <p class="footer-note">
          ?? For support, contact the bus operator
        </p>
      </div>
    </div>

    <div class="action-buttons no-print">
      <button class="btn btn-print" (click)="printTicket()">
        ??? Print Ticket
      </button>
      <button class="btn btn-home" (click)="goToHome()">
        ?? Book Another Ticket
      </button>
    </div>

    <app-seat-map
      [layout]="{rows: ['A','B','C','D','E'], seatsPerRow: [1,2,3,4], aisleAfter: 1}"
      [bookedSeats]="bookedFromApi"
      (selectionChange)="onSeatsChanged($event)">
    </app-seat-map>

    <p style="color:#fff; margin-top:8px;">
      Selected: {{ selectedSeats.length ? selectedSeats.join(', ') : 'None' }}
    </p>
  </div>
</div>
