import { Component, EventEmitter, Input, Output } from '@angular/core';

export interface SeatLayout {
  rows: string[];           // e.g., ['A','B','C','D']
  seatsPerRow: number[];    // e.g., [1,2,3,4] or with aisle: [1,2, null, 3,4]
  aisleAfter?: number;      // optional: add gap after this column index
}

@Component({
  selector: 'app-seat-map',
  templateUrl: './seat-map.component.html',
  styleUrls: ['./seat-map.component.css']
})
export class SeatMapComponent {
  @Input() layout: SeatLayout = {
    rows: ['A','B','C','D','E'],
    seatsPerRow: [1,2,3,4],
    aisleAfter: 2  // creates aisle between col 2 and 3
  };
  @Input() bookedSeats: string[] = [];
  @Input() disabled = false;
  @Output() selectionChange = new EventEmitter<string[]>();

  selected = new Set<string>();

  get rows() { return this.layout.rows; }
  get cols() { return this.layout.seatsPerRow; }

  isBooked(seat: string): boolean {
    return this.bookedSeats?.includes(seat);
  }

  isSelected(seat: string): boolean {
    return this.selected.has(seat);
  }

  toggleSeat(seat: string) {
    console.log('Seat clicked:', seat, 'Booked:', this.isBooked(seat), 'Disabled:', this.disabled);
    if (this.disabled || this.isBooked(seat)) return;
    this.selected.has(seat) ? this.selected.delete(seat) : this.selected.add(seat);
    console.log('Selected seats:', [...this.selected]);
    this.selectionChange.emit([...this.selected]);
  }

  isAisleAfter(colIndex: number): boolean {
    return this.layout.aisleAfter === colIndex;
  }

  trackByIndex = (_: number, v: any) => v;
}
