import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable } from 'rxjs';
import { AvailableBus, BusScheduleDetails, BookTicketRequest, Ticket } from '../models/bus.model';

@Injectable({
  providedIn: 'root'
})
export class BusService {
  private apiUrl = 'http://localhost:5000/api';

  constructor(private http: HttpClient) {}

  searchBuses(from: string, to: string, journeyDate: string): Observable<AvailableBus[]> {
    const params = new HttpParams()
      .set('from', from)
      .set('to', to)
      .set('journeyDate', journeyDate);

    return this.http.get<AvailableBus[]>(`${this.apiUrl}/Bus/Availablebus`, { params });
  }

  getBusScheduleDetails(busScheduleId: string): Observable<BusScheduleDetails> {
    return this.http.get<BusScheduleDetails>(`${this.apiUrl}/Booking/schedule/${busScheduleId}`);
  }

  bookTicket(bookingData: BookTicketRequest): Observable<Ticket> {
    return this.http.post<Ticket>(`${this.apiUrl}/Booking/book`, bookingData);
  }

  getTicket(ticketId: string): Observable<Ticket> {
    return this.http.get<Ticket>(`${this.apiUrl}/Booking/${ticketId}`);
  }

  cancelTicket(ticketId: string): Observable<any> {
    return this.http.delete(`${this.apiUrl}/Booking/${ticketId}/cancel`);
  }
}
