import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BusService } from '../../services/bus.service';
import { Ticket } from '../../models/bus.model';

@Component({
  selector: 'app-booking-success',
  templateUrl: './booking-success.component.html',
  styleUrls: ['./booking-success.component.css']
})
export class BookingSuccessComponent implements OnInit {
  ticket: Ticket | null = null;
  tickets: any[] = [];  // Array of tickets with seat labels
  seatLabelsMap: Map<string, string> = new Map();
  loading = true;
  errorMessage = '';

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private busService: BusService
  ) {
    // Check for navigation state with tickets
    const navigation = this.router.getCurrentNavigation();
    if (navigation?.extras?.state) {
      this.tickets = navigation.extras.state['tickets'] || [];
      const labelsArray = navigation.extras.state['seatLabelsMap'] || [];
      this.seatLabelsMap = new Map(labelsArray);
    }
  }

  ngOnInit(): void {
    // If tickets were passed via state, use them
    if (this.tickets.length > 0) {
      this.ticket = this.tickets[0];  // Set first ticket for backward compatibility
      this.loading = false;
      return;
    }

    // Otherwise, try to load from route param (old flow)
    const ticketId = this.route.snapshot.paramMap.get('id');
    if (ticketId) {
      this.loadTicket(ticketId);
    } else {
      this.errorMessage = 'Invalid ticket ID';
      this.loading = false;
    }
  }

  loadTicket(ticketId: string): void {
    this.busService.getTicket(ticketId).subscribe({
      next: (ticket) => {
        this.ticket = ticket;
        this.tickets = [ticket];
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading ticket:', error);
        this.errorMessage = 'Failed to load ticket details';
        this.loading = false;
      }
    });
  }

  getSeatLabel(ticket: any): string {
    // Return seatLabel if available, otherwise return seatNumber
    return ticket.seatLabel || ticket.seatNumber;
  }

  printTicket(): void {
    window.print();
  }

  goToHome(): void {
    this.router.navigate(['/']);
  }

  formatTime(time: string): string {
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const formattedHour = hour % 12 || 12;
    return `${formattedHour}:${minutes} ${ampm}`;
  }

  formatDate(date: string): string {
    const d = new Date(date);
    return d.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  getStatusText(status: number): string {
    switch(status) {
      case 0: return 'Pending';
      case 1: return 'Confirmed';
      case 2: return 'Cancelled';
      default: return 'Unknown';
    }
  }

  getStatusClass(status: number): string {
    switch(status) {
      case 0: return 'status-pending';
      case 1: return 'status-confirmed';
      case 2: return 'status-cancelled';
      default: return '';
    }
  }
}
<div class="container">
  <div class="loading-spinner" *ngIf="loading">
    <div class="spinner"></div>
    <p>Loading ticket details...</p>
  </div>

  <div class="error-alert" *ngIf="errorMessage">
    {{ errorMessage }}
  </div>

  <div class="success-page" *ngIf="!loading && ticket">
    <div class="success-animation">
      <div class="checkmark-circle">
        <div class="checkmark">‚úì</div>
      </div>
      <h1>Booking Confirmed!</h1>
      <p *ngIf="tickets.length === 1">Your ticket has been successfully booked</p>
      <p *ngIf="tickets.length > 1">Your {{ tickets.length }} tickets have been successfully booked</p>
    </div>

    <div class="ticket-card" *ngFor="let tkt of tickets; let i = index" id="ticket-print-{{i}}">
      <div class="ticket-header">
        <div class="ticket-logo">üé´</div>
        <div class="ticket-title">
          <h2>E-Ticket {{ tickets.length > 1 ? '#' + (i + 1) : '' }}</h2>
          <p class="ticket-id">ID: {{ tkt.id }}</p>
        </div>
        <div [class]="'status-badge ' + getStatusClass(tkt.status)">
          {{ getStatusText(tkt.status) }}
        </div>
      </div>

      <div class="ticket-divider"></div>

      <div class="ticket-body">
        <div class="company-info">
          <h3>{{ tkt.companyName }}</h3>
          <p>{{ tkt.busName }}</p>
        </div>

        <div class="journey-details">
          <div class="journey-row">
            <div class="journey-point">
              <span class="label">From</span>
              <span class="value">{{ tkt.fromCity }}</span>
              <span class="boarding">Boarding: {{ tkt.boardingPoint }}</span>
            </div>

            <div class="journey-arrow">
              <div class="arrow-line"></div>
              <div class="arrow-head">‚Üí</div>
            </div>

            <div class="journey-point">
              <span class="label">To</span>
              <span class="value">{{ tkt.toCity }}</span>
              <span class="dropping">Dropping: {{ tkt.droppingPoint }}</span>
            </div>
          </div>
        </div>

        <div class="ticket-details-grid">
          <div class="detail-item">
            <span class="detail-label">üìÖ Journey Date</span>
            <span class="detail-value">{{ formatDate(tkt.journeyDate) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üïê Departure Time</span>
            <span class="detail-value">{{ formatTime(tkt.startTime) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üïê Arrival Time</span>
            <span class="detail-value">{{ formatTime(tkt.arrivalTime) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üí∫ Seat Number</span>
            <span class="detail-value seat-highlight">{{ getSeatLabel(tkt) }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üë§ Passenger Name</span>
            <span class="detail-value">{{ tkt.passengerName }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üì± Mobile Number</span>
            <span class="detail-value">{{ tkt.mobileNumber }}</span>
          </div>

          <div class="detail-item">
            <span class="detail-label">üìÖ Booking Date</span>
            <span class="detail-value">{{ formatDate(tkt.bookingDate) }}</span>
          </div>

          <div class="detail-item price-item">
            <span class="detail-label">üí∞ Fare</span>
            <span class="detail-value price-value">‡ß≥{{ tkt.price }}</span>
          </div>
        </div>
      </div>

      <div class="ticket-divider"></div>

      <div class="ticket-footer">
        <p class="footer-note">
          ‚ÑπÔ∏è Please carry a valid ID proof during your journey
        </p>
        <p class="footer-note">
          üìû For support, contact the bus operator
        </p>
      </div>
    </div>

    <div class="action-buttons no-print">
      <button class="btn btn-print" (click)="printTicket()">
        üñ®Ô∏è Print Ticket
      </button>
      <button class="btn btn-home" (click)="goToHome()">
        üöå Book Another Ticket
      </button>
    </div>
  </div>
</div>
