eg, #2ecc71, #27ae60);\n  color: white;\n}\n\n.btn-book[_ngcontent-%COMP%]:hover:not(:disabled) {\n  transform: translateY(-2px);\n  box-shadow: 0 6px 12px rgba(46, 204, 113, 0.4);\n}\n\n.btn-book[_ngcontent-%COMP%]:disabled {\n  background: #bdc3c7;\n  cursor: not-allowed;\n  transform: none;\n}\n\n@media (max-width: 1024px) {\n  .main-content[_ngcontent-%COMP%] {\n    grid-template-columns: 1fr;\n  }\n\n  .booking-section[_ngcontent-%COMP%] {\n    order: -1;\n  }\n}\n\n@media (max-width: 768px) {\n  .bus-header[_ngcontent-%COMP%] {\n    flex-direction: column;\n    gap: 15px;\n  }\n\n  .price-section[_ngcontent-%COMP%] {\n    text-align: left;\n  }\n\n  .journey-info[_ngcontent-%COMP%] {\n    flex-direction: column;\n    gap: 20px;\n    align-items: flex-start;\n  }\n\n  .route-section[_ngcontent-%COMP%] {\n    width: 100%;\n    flex-wrap: wrap;\n  }\n\n  .seat[_ngcontent-%COMP%] {\n    width: 45px;\n    height: 45px;\n  }\n\n  .bus-layout[_ngcontent-%COMP%] {\n    padding: 20px;\n  }\n}\n\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
