import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { forkJoin } from 'rxjs';
import { BusService } from '../../services/bus.service';
import { BusScheduleDetails, Seat, SeatStatus, BookTicketRequest } from '../../models/bus.model';

@Component({
  selector: 'app-seat-selection',
  templateUrl: './seat-selection.component.html',
  styleUrls: ['./seat-selection.component.css']
})
export class SeatSelectionComponent implements OnInit {
  busScheduleId: string = '';
  busDetails: BusScheduleDetails | null = null;
  selectedSeat: Seat | null = null;
  selectedSeats: string[] = [];  // Store seat numbers from API
  selectedSeatIds: Set<string> = new Set();  // Track selected seat IDs
  bookingForm!: FormGroup;
  loading = false;
  bookingInProgress = false;
  errorMessage = '';
  successMessage = '';
  
  SeatStatus = SeatStatus;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private fb: FormBuilder,
    private busService: BusService
  ) {}

  ngOnInit(): void {
    this.busScheduleId = this.route.snapshot.paramMap.get('id') || '';
    
    this.bookingForm = this.fb.group({
      passengerName: ['', [Validators.required, Validators.minLength(2), Validators.maxLength(100)]],
      mobileNumber: ['', [Validators.required, Validators.pattern(/^[0-9+]{10,20}$/)]],
      boardingPoint: ['', Validators.required],
      droppingPoint: ['', Validators.required]
    });

    this.loadBusDetails();
  }

  loadBusDetails(): void {
    this.loading = true;
    this.errorMessage = '';

    this.busService.getBusScheduleDetails(this.busScheduleId).subscribe({
      next: (details) => {
        this.busDetails = details;
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading bus details:', error);
        this.errorMessage = 'Failed to load bus details. Please try again.';
        this.loading = false;
      }
    });
  }

  getSeatLayout(): Seat[][] {
    if (!this.busDetails || !this.busDetails.seats) return [];

    const maxRow = Math.max(...this.busDetails.seats.map(s => s.row));
    const maxCol = Math.max(...this.busDetails.seats.map(s => s.column));
    
    const layout: Seat[][] = [];
    
    for (let row = 1; row <= maxRow; row++) {
      const rowSeats: Seat[] = [];
      for (let col = 1; col <= maxCol; col++) {
        const seat = this.busDetails.seats.find(s => s.row === row && s.column === col);
        if (seat) {
          rowSeats.push(seat);
        }
      }
      if (rowSeats.length > 0) {
        layout.push(rowSeats);
      }
    }
    
    return layout;
  }

  selectSeat(seat: Seat): void {
    if (seat.status === SeatStatus.Available) {
      this.selectedSeat = seat;
      this.selectedSeats = [seat.seatNumber];
      this.selectedSeatIds.clear();
      this.selectedSeatIds.add(seat.id);
      this.errorMessage = '';
    }
  }

  selectSeatMultiple(seat: Seat): void {
    if (seat.status !== SeatStatus.Available) return;
    
    // Toggle selection
    if (this.selectedSeatIds.has(seat.id)) {
      this.selectedSeatIds.delete(seat.id);
      this.selectedSeats = this.selectedSeats.filter(s => s !== seat.seatNumber);
    } else {
      this.selectedSeatIds.add(seat.id);
      this.selectedSeats.push(seat.seatNumber);
    }
    
    // Update selectedSeat for backward compatibility
    if (this.selectedSeats.length > 0) {
      this.selectedSeat = seat;
    } else {
      this.selectedSeat = null;
    }
    
    this.errorMessage = '';
    console.log('Selected seats:', this.selectedSeats);
    console.log('Selected IDs:', [...this.selectedSeatIds]);
  }

  getSeatClass(seat: Seat): string {
    const classes = ['seat'];
    
    if (seat.status === SeatStatus.Available) {
      classes.push('available');
      if (this.selectedSeatIds.has(seat.id)) {
        classes.push('selected');
      }
    } else if (seat.status === SeatStatus.Booked) {
      classes.push('booked');
    } else if (seat.status === SeatStatus.Sold) {
      classes.push('sold');
    }
    
    return classes.join(' ');
  }

  getSeatIcon(seat: Seat): string {
    if (this.selectedSeatIds.has(seat.id)) return '✓';
    if (seat.status === SeatStatus.Available) return '💺';
    if (seat.status === SeatStatus.Booked) return '🔒';
    if (seat.status === SeatStatus.Sold) return '✖';
    return '';
  }

  onBookTicket(): void {
    if (this.selectedSeats.length === 0) {
      this.errorMessage = 'Please select at least one seat.';
      return;
    }

    if (this.bookingForm.invalid) {
      this.markFormGroupTouched(this.bookingForm);
      this.errorMessage = 'Please fill in all required fields correctly.';
      return;
    }

    this.bookingInProgress = true;
    this.errorMessage = '';
    this.successMessage = '';

    // Get seat objects for selected seat IDs
    const selectedSeatObjects = this.busDetails?.seats.filter(s => 
      this.selectedSeatIds.has(s.id)
    ) || [];

    if (selectedSeatObjects.length === 0) {
      this.errorMessage = 'Unable to find selected seats. Please try again.';
      this.bookingInProgress = false;
      return;
    }

    console.log('Booking seats:', selectedSeatObjects.map(s => s.seatNumber));

    // Book all seats - make multiple API calls
    const bookingRequests = selectedSeatObjects.map(seat => {
      const bookingRequest: BookTicketRequest = {
        busScheduleId: this.busScheduleId,
        seatId: seat.id,
        passengerName: this.bookingForm.value.passengerName,
        mobileNumber: this.bookingForm.value.mobileNumber,
        boardingPoint: this.bookingForm.value.boardingPoint,
        droppingPoint: this.bookingForm.value.droppingPoint
      };
      return this.busService.bookTicket(bookingRequest);
    });

    // Use forkJoin to wait for all bookings to complete
    forkJoin(bookingRequests).subscribe({
      next: (tickets) => {
        console.log('All tickets booked:', tickets);
        this.successMessage = `Booking successful! ${tickets.length} seat(s) booked.`;
        this.bookingInProgress = false;
        
        // Redirect to success page with first ticket ID
        setTimeout(() => {
          this.router.navigate(['/booking-success', tickets[0].id]);
        }, 2000);
      },
      error: (error) => {
        console.error('Booking error:', error);
        this.errorMessage = error.error?.message || 'Booking failed. The seat(s) may no longer be available.';
        this.bookingInProgress = false;
        
        // Reload bus details to refresh seat availability
        this.loadBusDetails();
        this.selectedSeats = [];
        this.selectedSeat = null;
      }
    });
  }

  goBack(): void {
    this.router.navigate(['/']);
  }

  formatTime(time: string): string {
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const formattedHour = hour % 12 || 12;
    return `${formattedHour}:${minutes} ${ampm}`;
  }

  formatDate(date: string): string {
    const d = new Date(date);
    return d.toLocaleDateString('en-US', { 
      weekday: 'short', 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  }

  private markFormGroupTouched(formGroup: FormGroup): void {
    Object.keys(formGroup.controls).forEach(key => {
      const control = formGroup.get(key);
      control?.markAsTouched();
    });
  }

  // Helper methods for seat-map component
  getBookedSeats(): string[] {
    if (!this.busDetails?.seats) return [];
    return this.busDetails.seats
      .filter(s => s.status === SeatStatus.Booked || s.status === SeatStatus.Sold)
      .map(s => s.seatNumber);
  }

  onSeatsChanged(seats: string[]): void {
    console.log('=== onSeatsChanged ===');
    console.log('Received seats:', seats);
    this.selectedSeats = seats;
    
    // For single-seat selection, update selectedSeat for booking form
    if (seats.length > 0 && this.busDetails?.seats) {
      const seatLabel = seats[0]; // first (and only in single-select mode)
      console.log('Looking for seat with label:', seatLabel);
      console.log('Available API seats:', this.busDetails.seats.map(s => s.seatNumber));
      
      // Try to match the seat label to API seat
      this.selectedSeat = this.busDetails.seats.find(s => s.seatNumber === seatLabel) || null;
      
      // If not found, create a mock seat for testing (you should remove this later)
      if (!this.selectedSeat) {
        console.warn('Seat not found in API data, using first available seat');
        this.selectedSeat = this.busDetails.seats.find(s => s.status === SeatStatus.Available) || null;
      }
      
      console.log('Selected seat object:', this.selectedSeat);
    } else {
      this.selectedSeat = null;
      console.log('No seats selected');
    }
  }
}
<div class="container">
  <div class="loading-spinner" *ngIf="loading">
    <div class="spinner"></div>
    <p>Loading bus details...</p>
  </div>

  <div class="error-alert" *ngIf="errorMessage">
    {{ errorMessage }}
  </div>

  <div class="success-alert" *ngIf="successMessage">
    ? {{ successMessage }}
  </div>

  <div *ngIf="!loading && busDetails">
    <div class="bus-info-card">
      <button class="btn-back" (click)="goBack()">
        Back to Search
      </button>

      <div class="bus-header">
        <div class="company-section">
          <h1>{{ busDetails.companyName }}</h1>
          <p class="bus-name">{{ busDetails.busName }} ({{ busDetails.busType }})</p>
        </div>
        <div class="price-section">
          <span class="price-label">Fare</span>
          <span class="price">?{{ busDetails.price }}</span>
        </div>
      </div>

      <div class="journey-info">
        <div class="route-section">
          <div class="route-point">
            <span class="icon">📍</span>
            <div>
              <p class="city">{{ busDetails.fromCity }}</p>
              <p class="time">{{ formatTime(busDetails.startTime) }}</p>
            </div>
          </div>
          
          <div class="route-line">
            <div class="arrow">→</div>
          </div>
          
          <div class="route-point">
            <span class="icon">📍</span>
            <div>
              <p class="city">{{ busDetails.toCity }}</p>
              <p class="time">{{ formatTime(busDetails.arrivalTime) }}</p>
            </div>
          </div>
        </div>
        
        <div class="date-section">
          <span class="icon">📅</span>
          <span>{{ formatDate(busDetails.journeyDate) }}</span>
        </div>
      </div>
    </div>

    <div class="main-content">
      <div class="seat-section">
        <h2>🪑 Select Your Seat</h2>
        
        <div class="seat-legend">
          <div class="legend-item">
            <span class="seat available-demo">💺</span>
            <span>Available</span>
          </div>
          <div class="legend-item">
            <span class="seat selected-demo">✓</span>
            <span>Selected</span>
          </div>
          <div class="legend-item">
            <span class="seat booked-demo">🔒</span>
            <span>Booked</span>
          </div>
          <div class="legend-item">
            <span class="seat sold-demo">✖</span>
            <span>Sold</span>
          </div>
        </div>

        <div class="bus-layout">
          <div class="steering">🚗 Driver</div>
          
          <div class="seats-grid">
            <div class="seat-row" *ngFor="let row of getSeatLayout(); let rowIndex = index">
              <div 
                *ngFor="let seat of row" 
                [class]="getSeatClass(seat)"
                (click)="selectSeatMultiple(seat)"
                [title]="'Seat ' + seat.seatNumber">
                <span class="seat-icon">{{ getSeatIcon(seat) }}</span>
                <span class="seat-number">{{ seat.seatNumber }}</span>
              </div>
              
              <!-- Add aisle space in the middle for typical bus layout -->
              <div class="aisle" *ngIf="row.length > 2 && rowIndex === 0"></div>
            </div>
          </div>
        </div>

        <div class="selected-seat-info" *ngIf="selectedSeats.length > 0">
          <div class="info-badge">
            <strong>Selected Seat:</strong> {{ selectedSeats.join(', ') }}
          </div>
        </div>
      </div>

      <div class="booking-section">
        <h2>📝 Booking Details</h2>
        
        <form [formGroup]="bookingForm" (ngSubmit)="onBookTicket()">
          <div class="form-group">
            <label for="passengerName">
              <i class="icon">👤</i> Passenger Name *
            </label>
            <input 
              type="text" 
              id="passengerName" 
              formControlName="passengerName"
              class="form-control"
              placeholder="Enter full name"
              [class.error]="bookingForm.get('passengerName')?.invalid && bookingForm.get('passengerName')?.touched">
            <div class="error-message" *ngIf="bookingForm.get('passengerName')?.invalid && bookingForm.get('passengerName')?.touched">
              <span *ngIf="bookingForm.get('passengerName')?.errors?.['required']">Name is required</span>
              <span *ngIf="bookingForm.get('passengerName')?.errors?.['minlength']">Name must be at least 2 characters</span>
            </div>
          </div>

          <div class="form-group">
            <label for="mobileNumber">
              📱 Mobile Number *
            </label>
            <input 
              type="tel" 
              id="mobileNumber" 
              formControlName="mobileNumber"
              class="form-control"
              placeholder="+8801XXXXXXXXX"
              [class.error]="bookingForm.get('mobileNumber')?.invalid && bookingForm.get('mobileNumber')?.touched">
            <div class="error-message" *ngIf="bookingForm.get('mobileNumber')?.invalid && bookingForm.get('mobileNumber')?.touched">
              <span *ngIf="bookingForm.get('mobileNumber')?.errors?.['required']">Mobile number is required</span>
              <span *ngIf="bookingForm.get('mobileNumber')?.errors?.['pattern']">Please enter a valid phone number</span>
            </div>
          </div>

          <div class="form-group">
            <label for="boardingPoint">
              🚏 Boarding Point *
            </label>
            <select 
              id="boardingPoint" 
              formControlName="boardingPoint"
              class="form-control"
              [class.error]="bookingForm.get('boardingPoint')?.invalid && bookingForm.get('boardingPoint')?.touched">
              <option value="">Select boarding point</option>
              <option *ngFor="let point of busDetails.boardingPoints" [value]="point">
                {{ point }}
              </option>
            </select>
            <div class="error-message" *ngIf="bookingForm.get('boardingPoint')?.invalid && bookingForm.get('boardingPoint')?.touched">
              Please select a boarding point
            </div>
          </div>

          <div class="form-group">
            <label for="droppingPoint">
              🏁 Dropping Point *
            </label>
            <select 
              id="droppingPoint" 
              formControlName="droppingPoint"
              class="form-control"
              [class.error]="bookingForm.get('droppingPoint')?.invalid && bookingForm.get('droppingPoint')?.touched">
              <option value="">Select dropping point</option>
              <option *ngFor="let point of busDetails.droppingPoints" [value]="point">
                {{ point }}
              </option>
            </select>
            <div class="error-message" *ngIf="bookingForm.get('droppingPoint')?.invalid && bookingForm.get('droppingPoint')?.touched">
              Please select a dropping point
            </div>
          </div>

          <div class="booking-summary" *ngIf="selectedSeats.length > 0">
            <h3>Booking Summary</h3>
            <div class="summary-item">
              <span>Seat Number:</span>
              <strong>{{ selectedSeats.join(', ') }}</strong>
            </div>
            <div class="summary-item">
              <span>Fare:</span>
              <strong>?{{ busDetails.price }}</strong>
            </div>
            <div class="summary-divider"></div>
            <div class="summary-item total">
              <span>Total Amount:</span>
              <strong>?{{ busDetails.price * selectedSeats.length }}</strong>
            </div>
          </div>

          <button 
            type="submit" 
            class="btn btn-book"
            [disabled]="selectedSeats.length === 0 || bookingForm.invalid || bookingInProgress">
            <span *ngIf="!bookingInProgress">
              {{ selectedSeats.length > 0 ? 'Confirm Booking' : 'Select a Seat First' }}
            </span>
            <span *ngIf="bookingInProgress">
              Processing Booking...
            </span>
          </button>
        </form>
      </div>
    </div>
  </div>
</div>
